mapper.select:

select 

from cruise_track
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}

mapper.delete:

delete from cruise_track
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}

mapper.update:

update cruise_track
set `name` = #{name,jdbcType=CHAR},
  dwell_time = #{dwellTime,jdbcType=INTEGER},
  speed = #{speed,jdbcType=INTEGER},
  flag = #{flag,jdbcType=INTEGER},
  preset_num = #{presetNum,jdbcType=CHAR},
  `status` = #{status,jdbcType=INTEGER}
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}
