mapper.select:

select 

from preset
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}

mapper.delete:

delete from preset
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}

mapper.update:

update preset


    `name` = #{name,jdbcType=CHAR},


    flag = #{flag,jdbcType=INTEGER},


where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}


update preset
set `name` = #{name,jdbcType=CHAR},
  flag = #{flag,jdbcType=INTEGER}
where channel_id = #{channelId,jdbcType=CHAR}
  and num = #{num,jdbcType=INTEGER}
