mapper.select:

select 

from t_priv_data
where user_id = #{userId,jdbcType=CHAR}
  and priv_code = #{privCode,jdbcType=VARCHAR}

mapper.delete:

delete from t_priv_data
where user_id = #{userId,jdbcType=CHAR}
  and priv_code = #{privCode,jdbcType=VARCHAR}

mapper.update:

update t_priv_data


    priv_type = #{privType,jdbcType=TINYINT},


    dmlflag = #{dmlflag,jdbcType=TINYINT},


    dmltime = #{dmltime,jdbcType=TIMESTAMP},


where user_id = #{userId,jdbcType=CHAR}
  and priv_code = #{privCode,jdbcType=VARCHAR}


update t_priv_data
set priv_type = #{privType,jdbcType=TINYINT},
  dmlflag = #{dmlflag,jdbcType=TINYINT},
  dmltime = #{dmltime,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=CHAR}
  and priv_code = #{privCode,jdbcType=VARCHAR}
