mapper.select:

select 

from user_tree
where user_id = #{userId,jdbcType=CHAR}
  and res_id = #{resId,jdbcType=CHAR}
  and parent_id = #{parentId,jdbcType=CHAR}

mapper.delete:

delete from user_tree
where user_id = #{userId,jdbcType=CHAR}
  and res_id = #{resId,jdbcType=CHAR}
  and parent_id = #{parentId,jdbcType=CHAR}

mapper.update:

update user_tree


    `name` = #{name,jdbcType=CHAR},


    previous_id = #{previousId,jdbcType=CHAR},


where user_id = #{userId,jdbcType=CHAR}
  and res_id = #{resId,jdbcType=CHAR}
  and parent_id = #{parentId,jdbcType=CHAR}


update user_tree
set `name` = #{name,jdbcType=CHAR},
  previous_id = #{previousId,jdbcType=CHAR}
where user_id = #{userId,jdbcType=CHAR}
  and res_id = #{resId,jdbcType=CHAR}
  and parent_id = #{parentId,jdbcType=CHAR}
