mapper.select:

select 

from trends
where itemid = #{itemid,jdbcType=BIGINT}
  and clock = #{clock,jdbcType=INTEGER}

mapper.delete:

delete from trends
where itemid = #{itemid,jdbcType=BIGINT}
  and clock = #{clock,jdbcType=INTEGER}

mapper.insert:

insert into trends (itemid, clock, num, 
  value_min, value_avg, value_max
  )
values (#{itemid,jdbcType=BIGINT}, #{clock,jdbcType=INTEGER}, #{num,jdbcType=INTEGER}, 
  #{valueMin,jdbcType=DOUBLE}, #{valueAvg,jdbcType=DOUBLE}, #{valueMax,jdbcType=DOUBLE}
  )

mapper.update:

update trends
set num = #{num,jdbcType=INTEGER},
  value_min = #{valueMin,jdbcType=DOUBLE},
  value_avg = #{valueAvg,jdbcType=DOUBLE},
  value_max = #{valueMax,jdbcType=DOUBLE}
where itemid = #{itemid,jdbcType=BIGINT}
  and clock = #{clock,jdbcType=INTEGER}
