  and clock = #{clock,jdbcType=INTEGER}

mapper.delete:

delete from trends_uint
where itemid = #{itemid,jdbcType=BIGINT}
  and clock = #{clock,jdbcType=INTEGER}

mapper.insert:

insert into trends_uint (itemid, clock, num, 
  value_min, value_avg, value_max
  )
values (#{itemid,jdbcType=BIGINT}, #{clock,jdbcType=INTEGER}, #{num,jdbcType=INTEGER}, 
  #{valueMin,jdbcType=BIGINT}, #{valueAvg,jdbcType=BIGINT}, #{valueMax,jdbcType=BIGINT}
  )

mapper.update:

update trends_uint
set num = #{num,jdbcType=INTEGER},
  value_min = #{valueMin,jdbcType=BIGINT},
  value_avg = #{valueAvg,jdbcType=BIGINT},
  value_max = #{valueMax,jdbcType=BIGINT}
where itemid = #{itemid,jdbcType=BIGINT}
  and clock = #{clock,jdbcType=INTEGER}
