mapper.select:

select 

from alarm_device_channel
where num = #{num,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=CHAR}

mapper.delete:

delete from alarm_device_channel
where num = #{num,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=CHAR}

mapper.update:

update alarm_device_channel


    channel_id = #{channelId,jdbcType=CHAR},


    `name` = #{name,jdbcType=CHAR},


    description = #{description,jdbcType=VARCHAR},


where num = #{num,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=CHAR}


update alarm_device_channel
set channel_id = #{channelId,jdbcType=CHAR},
  `name` = #{name,jdbcType=CHAR},
  description = #{description,jdbcType=VARCHAR}
where num = #{num,jdbcType=INTEGER}
  and device_id = #{deviceId,jdbcType=CHAR}
