mapper.select:

select 

from videowall_windows_group
where video_wall_id = #{videoWallId,jdbcType=INTEGER}
  and windows_num = #{windowsNum,jdbcType=INTEGER}
  and sub_windows_num = #{subWindowsNum,jdbcType=INTEGER}

mapper.delete:

delete from videowall_windows_group
where video_wall_id = #{videoWallId,jdbcType=INTEGER}
  and windows_num = #{windowsNum,jdbcType=INTEGER}
  and sub_windows_num = #{subWindowsNum,jdbcType=INTEGER}

mapper.update:

update videowall_windows_group


    turn_channel_group_id = #{turnChannelGroupId,jdbcType=INTEGER},


    description = #{description,jdbcType=VARCHAR},


where video_wall_id = #{videoWallId,jdbcType=INTEGER}
  and windows_num = #{windowsNum,jdbcType=INTEGER}
  and sub_windows_num = #{subWindowsNum,jdbcType=INTEGER}


update videowall_windows_group
set turn_channel_group_id = #{turnChannelGroupId,jdbcType=INTEGER},
  description = #{description,jdbcType=VARCHAR}
where video_wall_id = #{videoWallId,jdbcType=INTEGER}
  and windows_num = #{windowsNum,jdbcType=INTEGER}
  and sub_windows_num = #{subWindowsNum,jdbcType=INTEGER}
